import React from "react";
import { MapContainer, TileLayer, Polygon, Popup } from 'react-leaflet';
import { shopsData } from "../../data/shopsData";
import 'leaflet/dist/leaflet.css'

const ShopMap = () => {
   const position = [55.751244, 37.618423]
   return (
      <MapContainer center={position} zoom={10} style={{ width: '100vw', height: '100vh', }}>
         <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
         />
         {shopsData.map(shop => (
            <Polygon
               key={shop.id}
               positions={[[shop.lat, shop.lng], [shop.lat + 0.001, shop.lng], [shop.lat, shop.lng + 0.001]]}
               eventHandlers={{
                  click: () => alert(`You clicked on ${shop.name}`),
               }}
            >
               <Popup>
                  <div>
                     <h3>{shop.name}</h3>
                     <p>{shop.address}</p>
                     <p>{shop.contacts}</p>
                     <p>{shop.description}</p>
                     {shop.photos.map((photo, index) => (
                        <img key={index} src={photo} alt={`Photo ${index + 1}`} style={{ width: "100px" }} />
                     ))}
                  </div>
               </Popup>
            </Polygon>
         ))}
      </MapContainer>
   );
};

export default ShopMap;
