import { useEffect, useMemo, useState } from 'react';
import ShopMap from '../components/ShopMap';
import TodoList from '../components/TodoList';

const HomePage = () => {
   const [selectedShop, setSelectedShop] = useState(null);
   const [todoLists, setTodoLists] = useState({});

   useEffect(() => {
      const storedTodoLists = localStorage.getItem("todoList");
      if (storedTodoLists) {
         setTodoLists(JSON.parse(storedTodoLists));
      }
   }, []);
   const onShop = (obj) => {
      setSelectedShop(obj)
   }
   const updateTodoList = (shopId, todoList) => {
      setTodoLists(prevTodoLists => ({
         ...prevTodoLists,
         [`${shopId}`]: todoList,
      }));
   };
   useMemo(() => {
      localStorage.setItem("todoList", JSON.stringify({
         ...prevTodoLists,
         [`${shopId}`]: todoList
      }));
   })

   return (
      <div className="homepage">
         <ShopMap setSelectedShop={onShop} />
         <TodoList selectedShop={selectedShop}
            todoList={todoLists[selectedShop?.id] || []}
            updateTodoList={updateTodoList} />
      </div>
   )
}

export default HomePage;
