import React from "react";
import { MapContainer, TileLayer, Marker, Popup, Polygon } from "react-leaflet";
import L from "leaflet";
import { shopsData } from "../../data/shopsData";

const ShopMap = () => {
   const icon = new L.Icon({
      iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
      iconSize: [25, 41],
      iconAnchor: [12, 41],
      popupAnchor: [1, -34],
   });

   return (
      <MapContainer center={[55.751244, 37.618423]} zoom={13} scrollWheelZoom={false} style={{ height: "500px" }}>
         <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
         />
         {shopsData.map(shop => (
            <Polygon
               key={shop.id}
               positions={[[shop.lat, shop.lng], [shop.lat + 0.001, shop.lng], [shop.lat, shop.lng + 0.001]]}
               eventHandlers={{
                  click: () => alert(`You clicked on ${shop.name}`),
               }}
            >
               <Popup>
                  <div>
                     <h3>{shop.name}</h3>
                     <p>{shop.address}</p>
                     <p>{shop.contacts}</p>
                     <p>{shop.description}</p>
                     {shop.photos.map((photo, index) => (
                        <img key={index} src={photo} alt={`Photo ${index + 1}`} style={{ width: "100px" }} />
                     ))}
                  </div>
               </Popup>
            </Polygon>
         ))}
      </MapContainer>
   );
};

export default ShopMap;
