import React, { useState } from "react";
import styles from './TodoList.module.scss'

const TodoList = ({ selectedShop }) => {
   const [todoItem, setTodoItem] = useState("");
   const [todoList, setTodoList] = useState([]);

   const handleAddTodo = (selectName, e) => {
      if (todoItem.trim() !== "") {
         const obj = {}
         console.log(obj[`${selectName}`])
         if ((typeof obj[`${selectName}`] !== "undefined")) {
            obj[`${selectName}`].push(todoItem)
         } else {
            obj[`${selectName}`] = [todoItem]
         }
         console.log(obj)
      }
      setTodoList([...todoList,]);
      setTodoItem("");
   }
   return (
      <div className={styles.root}>
         {selectedShop && (
            <div className={styles.rootBlock}>
               <h2>TODO List for {selectedShop.name}</h2>
               <div className={styles.rootContent}>
                  <input
                     type="text"
                     value={todoItem}
                     onChange={e => setTodoItem(e.target.value)}
                     placeholder="Enter item to buy"
                  />
                  <button onClick={(e) => handleAddTodo(selectedShop.name, e)}>Add</button>
               </div>
               <ul className={styles.rootList}>
                  {/* {todoList['selectedShop.name'].map((todo, index) => (
                     <li key={todo.name}> {todo}</li>
                  ))} */}
               </ul>
            </div>
         )}
      </div>
   );
};

export default TodoList;
